import React from "react";
import { gql, useQuery, useMutation } from "@apollo/client";
import NewsDetails from "./dynamicDetails/NewsDetails";
import EmissionDetails from "./dynamicDetails/EmissionDetails";
import CauserieDetails from "./dynamicDetails/CauserieDetails";
import QuranDetails from "./dynamicDetails/QuranDetails";
import MusicDetails from "./dynamicDetails/MusicDetails";
import FictionDetails from "./dynamicDetails/FictionDetails";
import { useParams } from "react-router-dom";


const FETCH_AUDIO = gql`
  query fetchAudio($input: ID!) {
    audio(_id: $input) {
      _id
      title
      date_production
      productors
      nbr_invoice
      nbr_contract
      type_support
      nbr_support
      tech_comments
      quality
      language
      frequency
      synopsis
      type
      commentator
      event_location
      event_date
      sequence
      presenter
      preparation
      format
      version
      speaker
      causerie_reciter
      causerie_location
      quran_reciter
      recite_type
      singer
      interpreter
      composer
      music_writer
      musical_genre
      lyrics
      orchestra
      distribution
      author
      actor
      fiction_writer
      adaptation
    }
  }
`;
const DELETE_AUDIO = gql`
  mutation DeleteAudio($id: ID!){
    deleteAudio(_id: $id){
      _id 
      title
    }
  }
`


export default function AudioDetails() {
  const {_id} = useParams()
  const { data, loading, error } = useQuery(FETCH_AUDIO, {
    variables: { input: _id },
  });
  const [deleteAudio] = useMutation(DELETE_AUDIO, {variables: {_id}})
  if (loading) return <div>loading...</div>;
  if (error) return <div>error happened: {error.message}</div>;

  const audio = data.audio;

  const handleDelete = async () => {
    if(window.confirm()){
      await deleteAudio()
      return window.location.href = "/admin/audios"
    }
  }

  return (
    <div>
     <></>
    </div>
  );
}
