import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import { gql, useQuery } from "@apollo/client";

const FETCH_PLAYLIST = gql`
  query FetchPlaylist($id: ID!) {
    playlist(_id: $id) {
      name
      description
    }
  }
`;

export default function PlaylistMenu(props) {
  const [user, setUser] = useState();
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get("http://localhost:4000/user/getUser", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("userToken")}`,
          },
        });
        setUser(response.data);
      } catch (error) {
        console.log("can't get the user");
      }
    };
    fetchUser();
  }, []);
  const { data, loading, error } = useQuery(FETCH_PLAYLISTS, {
    variables: {
      id: user ? user._id : "",
    },
  });

  if (loading) return <div>loading</div>;
  if (error) return <div>error: {error.message}</div>;

  return (
    <div>
      <ul className="menu bg-base-200 w-56 rounded-box">
        <select className="menu-title" name="chosen" onChange={props.chosenChange}>
          {data.playlists.map((playlist) => (
            <option value={playlist._id}>{playlist.name}</option>
          ))}
        </select>
      </ul>
    </div>
  );
}
