import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function Home() {
  const scenes = [
    "music1.svg",
    "music2.svg",
    "music3.svg",
    "music4.svg",
    "music5.svg",
    "music6.svg",
    "music7.svg",
    "music8.svg",
  ];
  const [chosen, setChosen] = useState();
  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * scenes.length);
    setChosen(scenes[randomIndex]);
  }, []);

  return (
    <div className="bg-cover bg-center min-h-screen flex justify-center items-center">
      <div className="bg-red-200 w-4/5 md:w-1/2 p-8 rounded-lg shadow-lg">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">
          Société nationale de radiodiffusion et de télévision
        </h1>
        <p className="text-lg mb-8">
          shsdhlfa lskfjioawe dsja k sdfjjasdf asdklf
        </p>
        <div className="flex justify-center">
          <Link
            to="/login"
            className="btn-primary text-white mr-4 py-3 px-6 rounded-lg text-sm font-bold uppercase transition-all shadow-md hover:shadow-lg focus:outline-none focus:ring focus:ring-blue-300"
          >
            Login
          </Link>
          <Link
            to="/register"
            className="btn-secondary text-white py-3 px-6 rounded-lg text-sm font-bold uppercase transition-all shadow-md hover:shadow-lg focus:outline-none focus:ring focus:ring-blue-300"
          >
            Register
          </Link>
        </div>
      </div>
      <div className="hidden md:block md:w-1/2">
        <img
          src={`/assets/scenes/${chosen}`}
          className="w-3/5 mx-auto rounded-lg shadow-lg"
          alt="Music Scene"
        />
      </div>
    </div>
  );
}
