import React, { useEffect, useState } from "react";

export default function Home() {
  const scenes = ["music1.png", "music2.png", "music3.png", "music4.png", "music4.png", "music4.png", "music4.png"];
  const [chosen, setChosen] = useState();
  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * scenes.length);
    setChosen(scenes[randomIndex]);
  }, []);
  return (
    <div className="text-center flex h-screen">
      <div className="w-1/2">
        <h1 className="text-slate-700 text-4xl my-10">
          SNRT Audio managment App
        </h1>
      </div>
      <div className="w-1/2 mx-auto">
        <img
          src={`/assets/scenes/${chosen}`}
          className="w-3/5 mx-auto"
        ></img>
      </div>
    </div>
  );
}
