import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom"; // Assuming you're using React Router

export default function Home() {
  const scenes = [
    "music1.svg",
    "music2.svg",
    "music3.svg",
    "music4.svg",
    "music5.svg",
    "music6.svg",
    "music7.svg",
    "music8.svg",
  ];
  const [chosen, setChosen] = useState();
  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * scenes.length);
    setChosen(scenes[randomIndex]);
  }, []);

  return (
    <div className="text-center flex h-screen">
      <div className="w-1/2 flex flex-col justify-center">
        <div className="text-start p">
          <h1 className="text-4xl font-bold text-gray-800 mb-4">
            Welcome to SNRT Audio Management
          </h1>
          <p className="text-lg text-gray-600 mb-8">
            Your ultimate solution for organizing and enjoying your audio
            content.
          </p>
        </div>
        <div className="my-8">
          <Link to="/login" className="btn btn-primary text-white mr-4">
            Login
          </Link>
          <Link to="/register" className="btn btn-secondary text-white">
            Register
          </Link>
        </div>
      </div>
      <div className="w-1/2 flex justify-center items-center h-screen">
        <img src={`/assets/scenes/${chosen}`} className="w-3/5 mx-auto"></img>
      </div>
    </div>
  );
}
