import HeroFormComponent from "../../components/HeroFormComponent";

import React, { useState } from "react";
import axios from "axios";
import Success from "../../components/statusHandling/Success";

export default function AdminLogin() {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [status, setStatus] = useState(null);
  const [message, setMessage] = useState();

  const { username, password } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/admin/login", {
        username,
        password,
      });
      setStatus("success");
      setMessage(response.data.message);
      localStorage.setItem("adminToken", response.data.token);
      setTimeout(() => {
        window.location.href = "/admin/dashboard";
      }, 4000);
    } catch (err) {
      setStatus("failed");
      setMessage(err.response.data.message);
      console.log("Invalid credentials. Please try again.");
    }
  };

  return (
    <div>
      <HeroFormComponent
        title="Welcome Back Admin!"
        text="hello im hamza and im the best in the world"
        handleSubmit={handleSubmit}
        handleChange={handleChange}
        formData={formData}
        button="Login"
      />
      {status === "success" ? <Success message={message}/> : <></>}
      {status === "failed" ? <Error message={message}/> : <></>}
    </div>
  );
}
