import React from "react";
import AdminDrawerComponent from "../../../components/AdminDrawerComponent";

import { gql, useQuery } from "@apollo/client";
import { Link } from "react-router-dom";
import TableComponent from "../../../components/TableComponent";

const FETCH_AUDIOS = gql`
  query {
    audios {
      _id
      title
      date_production
      productors
      nbr_invoice
      nbr_contract
      type_support
      nbr_support
      tech_comments
      quality
      language
      frequency
      synopsis
      type
      commentator
      event_location
      event_date
      sequence
      presenter
      preparation
      format
      version
      speaker
      causerie_reciter
      causerie_location
      quran_reciter
      recite_type
      singer
      interpreter
      composer
      music_writer
      musical_genre
      lyrics
      orchestra
      distribution
      author
      actor
      fiction_writer
      adaptation
    }
  }
`;

export default function FetchAudios() {
  // fetch the audios
  const { data, loading, error } = useQuery(FETCH_AUDIOS);
  if (loading) return <div>loading...</div>;
  if (error) return <div>error: {error.message}</div>;

  // check if it's an admin
  const token = localStorage.getItem("adminToken");
  if (!token) {
    window.location.href = "/admin/login";
    return null;
  }
  return (
    <div className="flex flex-between h-screen">
      <AdminDrawerComponent />
      <div className="mx-auto flex justify-between">
        <TableComponent/>
      </div>
    </div>
  );
}
