import React from "react";
import AdminDrawerComponent from "../../../components/AdminDrawerComponent";

import { gql, useQuery } from "@apollo/client";
import { Link } from "react-router-dom";

// const FETCH_AUDIOS = gql`
//   query {
//     audios {
//       _id
//       title
//       date_production
//       productors
//       nbr_invoice
//       nbr_contract
//       type_support
//       nbr_support
//       tech_comments
//       quality
//       language
//       frequency
//       synopsis
//       type
//       commentator
//       event_location
//       event_date
//       sequence
//       presenter
//       preparation
//       format
//       version
//       speaker
//       causerie_reciter
//       causerie_location
//       quran_reciter
//       recite_type
//       singer
//       interpreter
//       composer
//       music_writer
//       musical_genre
//       lyrics
//       orchestra
//       distribution
//       author
//       actor
//       fiction_writer
//       adaptation
//     }
//   }
// `;

const FEED_AUDIOS = gql`
  query FeedAudios($offset: Int!, $limit: Int!) {
    feedAudios(offset: $offset, limit: $limit) {
      _id
      title
      date_production
      productors
      nbr_invoice
      nbr_contract
      type_support
      nbr_support
      tech_comments
      quality
      language
      frequency
      synopsis
      type
      commentator
      event_location
      event_date
      sequence
      presenter
      preparation
      format
      version
      speaker
      causerie_reciter
      causerie_location
      quran_reciter
      recite_type
      singer
      interpreter
      composer
      music_writer
      musical_genre
      lyrics
      orchestra
      distribution
      author
      actor
      fiction_writer
      adaptation
    }
  }
`;

export default function FetchAudios() {
  // fetch the audios
  const { data, loading, error, fetchMore } = useQuery(FEED_AUDIOS, {
    variables: {
      offset: 0,
      limit: 5,
    },
  });
  if (loading) return <div>loading...</div>;
  if (error) return <div>error: {error.message}</div>;

  // check if it's an admin
  const token = localStorage.getItem("adminToken");
  if (!token) {
    window.location.href = "/admin/login";
    return null;
  }
  return (
    <div className="flex flex-between h-screen">
      <AdminDrawerComponent />
      <div className="w-3/5 ml-96">
        <div className="my-10 uppercase text-2xl text-center">all audios</div>
        <div className="overflow-x-auto border-2 border-neutral-300 rounded-md">
          <table className="table">
            {/* head */}
            <thead>
              <tr>
                <th>TITLE</th>
                <th>TYPE</th>
                <th>QUALITY</th>
              </tr>
            </thead>
            <tbody>
              {/* row 1 */}
              {data.audios.map((audio) => (
                <tr>
                  <td>
                    <Link to={`/admin/audio-details/${audio._id}`}>
                      {audio.title}
                    </Link>
                  </td>
                  <td>{audio.type}</td>
                  <td>{audio.quality}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div class="join my-10 mx-auto text-center">
          <button class="join-item btn">prev</button>
          <button class="join-item btn">next</button>
        </div>
      </div>
    </div>
  );
}
