import React, { useEffect, useState } from "react";
import UserDrawerComponent from "../../components/UserDrawerComponent";
import { gql, useQuery } from "@apollo/client";
import { Link } from "react-router-dom";
import AudioComponent from "../../components/AudioComponent";

const FETCH_AUDIOS = gql`
  query {
    audios {
      _id
      title
      date_production
      productors
      nbr_invoice
      nbr_contract
      type_support
      nbr_support
      tech_comments
      quality
      language
      frequency
      synopsis
      type
      commentator
      event_location
      event_date
      sequence
      presenter
      preparation
      format
      version
      speaker
      causerie_reciter
      causerie_location
      quran_reciter
      recite_type
      singer
      interpreter
      composer
      music_writer
      musical_genre
      lyrics
      orchestra
      distribution
      author
      actor
      fiction_writer
      adaptation
    }
  }
`;

export default function UserAudios() {
  // fetch the audios
  const { data, loading, error } = useQuery(FETCH_AUDIOS);
  if (loading) return <div>loading...</div>;
  if (error) return <div>error: {error.message}</div>;
  // check if it's a user
  const token = localStorage.getItem("userToken");
  if (!token) {
    window.location.href = "/user/login";
    return null;
  }
  return (
    <div>
      <UserDrawerComponent />

      <div className="ml-96 flex flex-col gap-8 my-10">
        {data.audios.map((audio) => (
          <AudioComponent badge="new" toPath="/details"/>
        ))}
      </div>
    </div>
  );
}
